from flask import Flask, request

app = Flask(__name__)

beemoviescript = ""

@app.route('/', methods = ['POST', 'GET', 'HEAD', 'PUT', 'DELETE', 'CONNECT', 'TRACE', 'PATCH', beemoviescript])
def mainpage():
  if request.method == beemoviescript.upper():
    return "well played! web{FYI, that was the entire bee movie script}"
  else:
    return "You just sent a "+request.method+" request. But this is not really what I wanted."

if __name__ == "__main__":
  app.run(port=9000, host="0.0.0.0")
